export const src_dir: string
export const build_dir: string
export const dev: boolean
export const manifest: Manifest

export interface Manifest {
  server_routes: ServerRoute[];
  ignore: RegExp[];
  root_comp?: {
    default: SSRComponent<unknown>;
    preload?: Preload<unknown>;
  }
  error: SSRComponent
  pages: Route[]
};

export interface ServerRoute {
  pattern: RegExp;
  handlers: Record<string, Handler<Req, Res>>;
  params: (match: RegExpMatchArray) => Record<string, string>;
};

export interface SSRComponent<Props> {
  render(props: Props): {
    html: string
    head: string
    css: { code: string, map: unknown };
  }
}
